pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "./bUSDC.sol";
import "./VendorRegistry.sol";
import "./SubnameRegistrar.sol";

/**
 * @title PaymentRouter
 * @dev Handles bUSDC payments between users (P2P) and vendors
 * Supports P2P transfers, vendor payments, ENS resolution, QR/NFC payments
 * Emits Receipt events for transaction tracking
 */
contract PaymentRouter is Ownable, Pausable, ReentrancyGuard {
    // Payment types
    enum PaymentType {
        P2P,         // 0 - Peer-to-peer transfer
        VendorPay,   // 1 - Payment to vendor
        QRPay,       // 2 - QR code payment
        NFCPay       // 3 - NFC tap payment
    }
    
    // Payment status
    enum PaymentStatus {
        Pending,     // 0 - Payment initiated but not completed
        Completed,   // 1 - Payment successfully completed
        Failed,      // 2 - Payment failed
        Refunded     // 3 - Payment was refunded
    }
    
    // Receipt structure for on-chain payment records
    struct Receipt {
        bytes32 orderId;           // Unique order identifier
        string recipientENS;       // Recipient's ENS name (vendor or user)
        address sender;            // Sender's address
        address recipient;         // Recipient's address
        uint256 amountGHS;         // Amount in GHS (for display)
        uint256 amountUSDC;        // Amount in bUSDC (actual payment)
        uint256 fxRate;            // Exchange rate used (GHS to USDC)
        uint256 timestamp;         // Payment timestamp
        PaymentType paymentType;   // Type of payment
        PaymentStatus status;      // Payment status
        string metadata;           // Additional metadata (JSON string)
        bool isVendorPayment;      // Whether recipient is a vendor
    }
    
    // State variables
    bUSDC public busdcToken;
    VendorRegistry public vendorRegistry;
    SubnameRegistrar public subnameRegistrar;
    
    // Exchange rate management
    uint256 public currentFxRate = 1e6; // 1 GHS = 1 USDC (6 decimals) - default rate
    address public fxRateUpdater; // Address allowed to update exchange rates
    
    // Fee structure
    uint256 public platformFeeBps = 25; // 0.25% platform fee (25 basis points)
    address public feeRecipient; // Address to receive platform fees
    
    // Payment tracking
    mapping(bytes32 => Receipt) public receipts; // orderId -> Receipt
    mapping(address => bytes32[]) public userReceipts; // user -> array of orderIds
    mapping(address => uint256) public vendorEarnings; // vendor -> total earnings
    mapping(address => uint256) public userEarnings; // user -> total P2P earnings
    
    // Order ID generation
    uint256 private orderCounter = 0;
    
    // Events
    event PaymentInitiated(
        bytes32 indexed orderId,
        address indexed sender,
        address indexed recipient,
        uint256 amountUSDC,
        PaymentType paymentType
    );
    
    event PaymentCompleted(
        bytes32 indexed orderId,
        address indexed sender,
        address indexed recipient,
        uint256 amountUSDC,
        uint256 platformFee,
        uint256 recipientAmount
    );
    
    event PaymentFailed(
        bytes32 indexed orderId,
        address indexed sender,
        address indexed recipient,
        string reason
    );
    
    event PaymentRefunded(
        bytes32 indexed orderId,
        address indexed sender,
        address indexed recipient,
        uint256 amountUSDC
    );
    
    event ExchangeRateUpdated(uint256 oldRate, uint256 newRate);
    event PlatformFeeUpdated(uint256 oldFee, uint256 newFee);
    event FeeRecipientUpdated(address oldRecipient, address newRecipient);
    
    // Modifiers
    modifier onlyActiveVendor(address vendor) {
        require(vendorRegistry.isActiveVendor(vendor), "PaymentRouter: Vendor not active");
        _;
    }
    
    modifier validRecipient(address recipient) {
        require(recipient != address(0), "PaymentRouter: Invalid recipient address");
        require(recipient != msg.sender, "PaymentRouter: Cannot send to self");
        _;
    }
    
    modifier onlyFxRateUpdater() {
        require(msg.sender == fxRateUpdater || msg.sender == owner(), "PaymentRouter: Not authorized to update exchange rate");
        _;
    }
    
    modifier validOrderId(bytes32 orderId) {
        require(receipts[orderId].orderId != bytes32(0), "PaymentRouter: Invalid order ID");
        _;
    }
    
    constructor(
        address _busdcToken,
        address _vendorRegistry,
        address _subnameRegistrar,
        address _feeRecipient
    ) Ownable(msg.sender) {
        busdcToken = bUSDC(_busdcToken);
        vendorRegistry = VendorRegistry(_vendorRegistry);
        subnameRegistrar = SubnameRegistrar(payable(_subnameRegistrar));
        feeRecipient = _feeRecipient;
        fxRateUpdater = msg.sender;
    }
    
    /**
     * @dev Send P2P payment to any address
     * @param recipient Recipient's address
     * @param amountGHS Amount in GHS
     * @param metadata Optional metadata
     * @return orderId Generated order ID
     */
    function sendP2PPayment(
        address recipient,
        uint256 amountGHS,
        string memory metadata
    ) external validRecipient(recipient) whenNotPaused returns (bytes32 orderId) {
        uint256 amountUSDC = (amountGHS * currentFxRate) / 1e6;
        require(amountUSDC > 0, "PaymentRouter: Amount too small");
        
        orderId = _generateOrderId();
        
        // Create receipt
        receipts[orderId] = Receipt({
            orderId: orderId,
            recipientENS: _getRecipientENS(recipient),
            sender: msg.sender,
            recipient: recipient,
            amountGHS: amountGHS,
            amountUSDC: amountUSDC,
            fxRate: currentFxRate,
            timestamp: block.timestamp,
            paymentType: PaymentType.P2P,
            status: PaymentStatus.Pending,
            metadata: metadata,
            isVendorPayment: vendorRegistry.isVendor(recipient)
        });
        
        userReceipts[msg.sender].push(orderId);
        userReceipts[recipient].push(orderId);
        
        emit PaymentInitiated(orderId, msg.sender, recipient, amountUSDC, PaymentType.P2P);
        
        return orderId;
    }
    
    /**
     * @dev Send payment to vendor (with QR/NFC support)
     * @param vendor Vendor's address
     * @param amountGHS Amount in GHS
     * @param metadata JSON string with payment details
     * @param paymentType QR or NFC payment type
     * @return orderId Generated order ID
     */
    function sendVendorPayment(
        address vendor,
        uint256 amountGHS,
        string memory metadata,
        PaymentType paymentType
    ) external onlyActiveVendor(vendor) whenNotPaused returns (bytes32 orderId) {
        require(paymentType == PaymentType.QRPay || paymentType == PaymentType.VendorPay, 
                "PaymentRouter: Invalid payment type for vendor");
        
        uint256 amountUSDC = (amountGHS * currentFxRate) / 1e6;
        require(amountUSDC > 0, "PaymentRouter: Amount too small");
        
        orderId = _generateOrderId();
        
        // Create receipt
        receipts[orderId] = Receipt({
            orderId: orderId,
            recipientENS: _getVendorENS(vendor),
            sender: msg.sender,
            recipient: vendor,
            amountGHS: amountGHS,
            amountUSDC: amountUSDC,
            fxRate: currentFxRate,
            timestamp: block.timestamp,
            paymentType: paymentType,
            status: PaymentStatus.Pending,
            metadata: metadata,
            isVendorPayment: true
        });
        
        userReceipts[msg.sender].push(orderId);
        userReceipts[vendor].push(orderId);
        
        emit PaymentInitiated(orderId, msg.sender, vendor, amountUSDC, paymentType);
        
        return orderId;
    }
    
    /**
     * @dev Complete a payment by transferring bUSDC tokens
     * @param orderId Order ID to complete
     */
    function completePayment(bytes32 orderId) external nonReentrant validOrderId(orderId) {
        Receipt storage receipt = receipts[orderId];
        require(receipt.status == PaymentStatus.Pending, "PaymentRouter: Payment not pending");
        require(receipt.sender == msg.sender, "PaymentRouter: Only sender can complete payment");
        
        // Check sender has sufficient balance
        require(
            busdcToken.balanceOf(msg.sender) >= receipt.amountUSDC,
            "PaymentRouter: Insufficient bUSDC balance"
        );
        
        // Calculate fees (only for vendor payments)
        uint256 platformFee = 0;
        uint256 recipientAmount = receipt.amountUSDC;
        
        if (receipt.isVendorPayment) {
            platformFee = (receipt.amountUSDC * platformFeeBps) / 10000;
            recipientAmount = receipt.amountUSDC - platformFee;
        }
        
        // Transfer tokens
        require(
            busdcToken.transferFrom(msg.sender, address(this), receipt.amountUSDC),
            "PaymentRouter: Transfer failed"
        );
        
        // Distribute payment
        if (platformFee > 0) {
            require(
                busdcToken.transfer(feeRecipient, platformFee),
                "PaymentRouter: Fee transfer failed"
            );
        }
        
        require(
            busdcToken.transfer(receipt.recipient, recipientAmount),
            "PaymentRouter: Recipient transfer failed"
        );
        
        // Update receipt and earnings
        receipt.status = PaymentStatus.Completed;
        
        if (receipt.isVendorPayment) {
            vendorEarnings[receipt.recipient] += recipientAmount;
        } else {
            userEarnings[receipt.recipient] += recipientAmount;
        }
        
        emit PaymentCompleted(orderId, receipt.sender, receipt.recipient, receipt.amountUSDC, platformFee, recipientAmount);
    }
    
    /**
     * @dev Mark a payment as failed (admin or recipient only)
     * @param orderId Order ID to mark as failed
     * @param reason Reason for failure
     */
    function markPaymentFailed(bytes32 orderId, string memory reason) external validOrderId(orderId) {
        Receipt storage receipt = receipts[orderId];
        require(receipt.status == PaymentStatus.Pending, "PaymentRouter: Payment not pending");
        require(
            msg.sender == receipt.recipient || msg.sender == owner(),
            "PaymentRouter: Only recipient or admin can mark as failed"
        );
        
        receipt.status = PaymentStatus.Failed;
        
        emit PaymentFailed(orderId, receipt.sender, receipt.recipient, reason);
    }
    
    /**
     * @dev Refund a completed payment (admin only)
     * @param orderId Order ID to refund
     */
    function refundPayment(bytes32 orderId) external onlyOwner validOrderId(orderId) nonReentrant {
        Receipt storage receipt = receipts[orderId];
        require(receipt.status == PaymentStatus.Completed, "PaymentRouter: Payment not completed");
        
        // Calculate refund amount (full amount, fees are not refunded)
        uint256 refundAmount = receipt.amountUSDC;
        
        // Transfer refund to sender
        require(
            busdcToken.transfer(receipt.sender, refundAmount),
            "PaymentRouter: Refund transfer failed"
        );
        
        // Update receipt and earnings
        receipt.status = PaymentStatus.Refunded;
        
        if (receipt.isVendorPayment) {
            vendorEarnings[receipt.recipient] -= refundAmount;
        } else {
            userEarnings[receipt.recipient] -= refundAmount;
        }
        
        emit PaymentRefunded(orderId, receipt.sender, receipt.recipient, refundAmount);
    }
    
    /**
     * @dev Update exchange rate (GHS to USDC)
     * @param newRate New exchange rate (6 decimals)
     */
    function updateExchangeRate(uint256 newRate) external onlyFxRateUpdater {
        require(newRate > 0, "PaymentRouter: Exchange rate must be positive");
        
        uint256 oldRate = currentFxRate;
        currentFxRate = newRate;
        
        emit ExchangeRateUpdated(oldRate, newRate);
    }
    
    /**
     * @dev Update platform fee
     * @param newFeeBps New fee in basis points (100 = 1%)
     */
    function updatePlatformFee(uint256 newFeeBps) external onlyOwner {
        require(newFeeBps <= 1000, "PaymentRouter: Fee cannot exceed 10%");
        
        uint256 oldFee = platformFeeBps;
        platformFeeBps = newFeeBps;
        
        emit PlatformFeeUpdated(oldFee, newFeeBps);
    }
    
    /**
     * @dev Update fee recipient
     * @param newRecipient New fee recipient address
     */
    function updateFeeRecipient(address newRecipient) external onlyOwner {
        require(newRecipient != address(0), "PaymentRouter: Fee recipient cannot be zero address");
        
        address oldRecipient = feeRecipient;
        feeRecipient = newRecipient;
        
        emit FeeRecipientUpdated(oldRecipient, newRecipient);
    }
    
    /**
     * @dev Update FX rate updater address
     * @param newUpdater New FX rate updater address
     */
    function updateFxRateUpdater(address newUpdater) external onlyOwner {
        require(newUpdater != address(0), "PaymentRouter: FX rate updater cannot be zero address");
        fxRateUpdater = newUpdater;
    }
    
    /**
     * @dev Get receipt by order ID
     * @param orderId Order ID to query
     * @return receipt Receipt struct
     */
    function getReceipt(bytes32 orderId) external view validOrderId(orderId) returns (Receipt memory receipt) {
        return receipts[orderId];
    }
    
    /**
     * @dev Get user's receipt IDs
     * @param user User address
     * @param offset Starting index
     * @param limit Maximum number of receipts to return
     * @return orderIds Array of order IDs
     */
    function getUserReceipts(
        address user,
        uint256 offset,
        uint256 limit
    ) external view returns (bytes32[] memory orderIds) {
        bytes32[] storage userReceiptList = userReceipts[user];
        require(offset < userReceiptList.length, "PaymentRouter: Offset out of bounds");
        
        uint256 end = offset + limit;
        if (end > userReceiptList.length) {
            end = userReceiptList.length;
        }
        
        bytes32[] memory result = new bytes32[](end - offset);
        for (uint256 i = offset; i < end; i++) {
            result[i - offset] = userReceiptList[i];
        }
        
        return result;
    }
    
    /**
     * @dev Get vendor's total earnings
     * @param vendor Vendor address
     * @return earnings Total earnings in bUSDC
     */
    function getVendorEarnings(address vendor) external view returns (uint256 earnings) {
        return vendorEarnings[vendor];
    }
    
    /**
     * @dev Get user's total P2P earnings
     * @param user User address
     * @return earnings Total P2P earnings in bUSDC
     */
    function getUserEarnings(address user) external view returns (uint256 earnings) {
        return userEarnings[user];
    }
    
    /**
     * @dev Calculate amount in USDC from GHS
     * @param amountGHS Amount in GHS
     * @return amountUSDC Amount in bUSDC
     */
    function calculateUSDCAmount(uint256 amountGHS) external view returns (uint256 amountUSDC) {
        return (amountGHS * currentFxRate) / 1e6;
    }
    
    /**
     * @dev Generate unique order ID
     * @return orderId Generated order ID
     */
    function _generateOrderId() internal returns (bytes32 orderId) {
        orderCounter++;
        return keccak256(abi.encodePacked(
            block.timestamp,
            block.number,
            msg.sender,
            orderCounter
        ));
    }
    
    /**
     * @dev Get vendor's ENS name
     * @param vendor Vendor address
     * @return ensName Vendor's ENS name
     */
    function _getVendorENS(address vendor) internal view returns (string memory ensName) {
        VendorRegistry.VendorProfile memory profile = vendorRegistry.getVendorProfile(vendor);
        return profile.ensName;
    }
    
    /**
     * @dev Get recipient's ENS name (vendor or user)
     * @param recipient Recipient address
     * @return ensName Recipient's ENS name
     */
    function _getRecipientENS(address recipient) internal view returns (string memory ensName) {
        if (vendorRegistry.isVendor(recipient)) {
            return _getVendorENS(recipient);
        }
        
        // For regular users, try to get ENS from subname registrar
        // This would be implemented when we have user ENS support
        return "";
    }
    
    /**
     * @dev Pause the contract (admin only)
     */
    function pause() external onlyOwner {
        _pause();
    }
    
    /**
     * @dev Unpause the contract (admin only)
     */
    function unpause() external onlyOwner {
        _unpause();
    }
    
    /**
     * @dev Emergency withdraw (admin only)
     * @param token Token address to withdraw (0 for ETH)
     * @param amount Amount to withdraw (0 for all)
     */
    function emergencyWithdraw(address token, uint256 amount) external onlyOwner {
        if (token == address(0)) {
            // Withdraw ETH
            uint256 ethAmount = amount == 0 ? address(this).balance : amount;
            require(ethAmount > 0, "PaymentRouter: No ETH to withdraw");
            
            (bool success, ) = payable(owner()).call{value: ethAmount}("");
            require(success, "PaymentRouter: ETH withdrawal failed");
        } else {
            // Withdraw ERC20 tokens
            require(token == address(busdcToken), "PaymentRouter: Can only withdraw bUSDC");
            
            uint256 tokenAmount = amount == 0 ? busdcToken.balanceOf(address(this)) : amount;
            require(tokenAmount > 0, "PaymentRouter: No tokens to withdraw");
            
            require(busdcToken.transfer(owner(), tokenAmount), "PaymentRouter: Token withdrawal failed");
        }
    }
}
","keccak256":"0xd2d73152d20df539255a30f07d955b362cd100e6ac87c26b0d25af49ddf6fc3e","license":"MIT"},"contracts/SubnameRegistrar.sol":{"content":"//SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "./VendorRegistry.sol";

// Real ENS Registry interface (Sepolia)
interface ENSRegistry {
    function setSubnodeRecord(
        bytes32 node,
        bytes32 label,
        address owner,
        address resolver,
        uint64 ttl
    ) external;
    
    function setResolver(bytes32 node, address resolver) external;
    function setOwner(bytes32 node, address owner) external;
    function owner(bytes32 node) external view returns (address);
    function resolver(bytes32 node) external view returns (address);
}

// Real ENS Resolver interface (Sepolia)
interface ENSResolver {
    function setAddr(bytes32 node, address a) external;
    function setText(bytes32 node, string calldata key, string calldata value) external;
    function addr(bytes32 node) external view returns (address);
    function text(bytes32 node, string calldata key) external view returns (string memory);
}

/**
 * @title SubnameRegistrar
 * @dev Manages ENS subnames under .tapngo.eth for verified vendors
 * Integrates with real Sepolia ENS registry for cross-chain resolution
 * This contract issues subnames only to vendors registered in VendorRegistry
 */
contract SubnameRegistrar is Ownable, Pausable {
    
    // State variables
    VendorRegistry public vendorRegistry;
    ENSRegistry public ensRegistry;
    ENSResolver public ensResolver;
    
    // ENS node for .tapngo.eth (real namehash for tapngo.eth on Sepolia)
    // This will be set when tapngo.eth is owned on Sepolia
    bytes32 public tapngoNode;
    
    // Sepolia ENS Registry and Resolver addresses
    address public constant SEPOLIA_ENS_REGISTRY = 0x00000000000C2E074eC69A0dFb2997BA6C7d2e1e;
    address public constant SEPOLIA_PUBLIC_RESOLVER = 0x231b0Ee14048e9dCcD1d247744d114a4EB5E8E63;
    
    // Mapping to track registered subnames
    mapping(string => address) public subnameToOwner; // subname -> owner address
    mapping(address => string[]) public ownerToSubnames; // owner -> array of subnames
    mapping(string => bool) public subnameExists; // subname -> exists
    
    // Subname registration fee (in wei)
    uint256 public registrationFee = 0; // Free for now, can be set by admin
    
    // Events
    event SubnameRegistered(
        string indexed subname,
        address indexed owner,
        address indexed vendor,
        uint256 timestamp
    );
    
    event SubnameTransferred(
        string indexed subname,
        address indexed oldOwner,
        address indexed newOwner
    );
    
    event SubnameRevoked(string indexed subname, address indexed owner);
    event RegistrationFeeUpdated(uint256 oldFee, uint256 newFee);
    
    // Modifiers
    modifier onlyVendor() {
        require(vendorRegistry.isVendor(msg.sender), "SubnameRegistrar: Only registered vendors can register subnames");
        _;
    }
    
    modifier onlyActiveVendor() {
        require(vendorRegistry.isActiveVendor(msg.sender), "SubnameRegistrar: Only active vendors can register subnames");
        _;
    }
    
    modifier onlySubnameOwner(string memory subname) {
        require(subnameToOwner[subname] == msg.sender, "SubnameRegistrar: Not the owner of this subname");
        _;
    }
    
    modifier onlyAdmin() {
        require(msg.sender == owner(), "SubnameRegistrar: Only admin can call this function");
        _;
    }
    
    constructor(
        address _vendorRegistry
    ) Ownable(msg.sender) {
        vendorRegistry = VendorRegistry(_vendorRegistry);
        ensRegistry = ENSRegistry(SEPOLIA_ENS_REGISTRY);
        ensResolver = ENSResolver(SEPOLIA_PUBLIC_RESOLVER);
        // tapngoNode will be set when tapngo.eth is owned
    }
    
    /**
     * @dev Register a new ENS subname for a vendor
     * @param subname The subname to register (e.g., "business" for business.tapngo.eth)
     */
    function registerSubname(string memory subname) external payable onlyActiveVendor whenNotPaused {
        require(bytes(subname).length > 0, "SubnameRegistrar: Subname cannot be empty");
        require(!subnameExists[subname], "SubnameRegistrar: Subname already exists");
        require(msg.value >= registrationFee, "SubnameRegistrar: Insufficient registration fee");
        require(tapngoNode != bytes32(0), "SubnameRegistrar: tapngo.eth node not set");
        
        // Get vendor profile to verify ENS name matches
        VendorRegistry.VendorProfile memory profile = vendorRegistry.getVendorProfile(msg.sender);
        require(bytes(profile.ensName).length > 0, "SubnameRegistrar: Vendor must have ENS name set");
        
        // Verify the subname matches the vendor's ENS name
        string memory expectedSubname = _extractSubname(profile.ensName);
        require(
            keccak256(bytes(subname)) == keccak256(bytes(expectedSubname)),
            "SubnameRegistrar: Subname must match vendor's registered ENS name"
        );
        
        // Register the subname
        subnameToOwner[subname] = msg.sender;
        subnameExists[subname] = true;
        ownerToSubnames[msg.sender].push(subname);
        
        // Register with real Sepolia ENS
        bytes32 label = keccak256(bytes(subname));
        ensRegistry.setSubnodeRecord(
            tapngoNode,
            label,
            msg.sender,
            address(ensResolver),
            0 // TTL = 0 means use default
        );
        
        // Set the address resolution
        bytes32 node = keccak256(abi.encodePacked(tapngoNode, label));
        ensResolver.setAddr(node, msg.sender);
        
        // Set text records for additional metadata
        ensResolver.setText(node, "description", profile.businessName);
        ensResolver.setText(node, "url", string(abi.encodePacked("https://tapngo.eth/", subname)));
        
        emit SubnameRegistered(subname, msg.sender, msg.sender, block.timestamp);
    }
    
    /**
     * @dev Transfer ownership of a subname to another address
     * @param subname The subname to transfer
     * @param newOwner The new owner address
     */
    function transferSubname(string memory subname, address newOwner) external onlySubnameOwner(subname) {
        require(newOwner != address(0), "SubnameRegistrar: New owner cannot be zero address");
        require(newOwner != msg.sender, "SubnameRegistrar: Cannot transfer to self");
        
        address oldOwner = subnameToOwner[subname];
        subnameToOwner[subname] = newOwner;
        
        // Update owner's subname list
        _removeSubnameFromOwner(oldOwner, subname);
        ownerToSubnames[newOwner].push(subname);
        
        // Update ENS record
        bytes32 label = keccak256(bytes(subname));
        bytes32 node = keccak256(abi.encodePacked(tapngoNode, label));
        ensRegistry.setSubnodeRecord(
            tapngoNode,
            label,
            newOwner,
            address(ensResolver),
            0
        );
        ensResolver.setAddr(node, newOwner);
        
        emit SubnameTransferred(subname, oldOwner, newOwner);
    }
    
    /**
     * @dev Revoke a subname (admin only)
     * @param subname The subname to revoke
     */
    function revokeSubname(string memory subname) external onlyAdmin {
        require(subnameExists[subname], "SubnameRegistrar: Subname does not exist");
        
        address owner = subnameToOwner[subname];
        
        // Remove from mappings
        delete subnameToOwner[subname];
        subnameExists[subname] = false;
        _removeSubnameFromOwner(owner, subname);
        
        // Clear ENS record (set owner to zero address)
        bytes32 label = keccak256(bytes(subname));
        ensRegistry.setSubnodeRecord(
            tapngoNode,
            label,
            address(0),
            address(0),
            0
        );
        
        emit SubnameRevoked(subname, owner);
    }
    
    /**
     * @dev Set the tapngo.eth node (admin only)
     * @param _tapngoNode The namehash of tapngo.eth
     */
    function setTapngoNode(bytes32 _tapngoNode) external onlyAdmin {
        require(_tapngoNode != bytes32(0), "SubnameRegistrar: Invalid node");
        tapngoNode = _tapngoNode;
    }
    
    /**
     * @dev Update ENS resolver address (admin only)
     * @param newResolver New resolver address
     */
    function updateResolver(address newResolver) external onlyAdmin {
        require(newResolver != address(0), "SubnameRegistrar: Resolver cannot be zero address");
        ensResolver = ENSResolver(newResolver);
    }
    
    /**
     * @dev Update registration fee (admin only)
     * @param newFee New registration fee in wei
     */
    function updateRegistrationFee(uint256 newFee) external onlyAdmin {
        uint256 oldFee = registrationFee;
        registrationFee = newFee;
        emit RegistrationFeeUpdated(oldFee, newFee);
    }
    
    /**
     * @dev Get all subnames owned by an address
     * @param owner Address to query
     * @return subnames Array of subnames owned by the address
     */
    function getSubnamesByOwner(address owner) external view returns (string[] memory subnames) {
        return ownerToSubnames[owner];
    }
    
    /**
     * @dev Check if a subname exists
     * @param subname Subname to check
     * @return exists True if subname exists
     */
    function isSubnameRegistered(string memory subname) external view returns (bool exists) {
        return subnameExists[subname];
    }
    
    /**
     * @dev Get owner of a subname
     * @param subname Subname to query
     * @return owner Address of the subname owner
     */
    function getSubnameOwner(string memory subname) external view returns (address owner) {
        return subnameToOwner[subname];
    }
    
    /**
     * @dev Extract subname from full ENS name
     * @param ensName Full ENS name (e.g., "business.tapngo.eth")
     * @return subname Just the subname part (e.g., "business")
     */
    function _extractSubname(string memory ensName) internal pure returns (string memory subname) {
        bytes memory ensBytes = bytes(ensName);
        uint256 dotIndex = 0;
        
        // Find the first dot
        for (uint256 i = 0; i < ensBytes.length; i++) {
            if (ensBytes[i] == '.') {
                dotIndex = i;
                break;
            }
        }
        
        require(dotIndex > 0, "SubnameRegistrar: Invalid ENS name format");
        
        // Extract subname
        bytes memory subnameBytes = new bytes(dotIndex);
        for (uint256 i = 0; i < dotIndex; i++) {
            subnameBytes[i] = ensBytes[i];
        }
        
        return string(subnameBytes);
    }
    
    /**
     * @dev Remove subname from owner's list
     * @param owner Owner address
     * @param subname Subname to remove
     */
    function _removeSubnameFromOwner(address owner, string memory subname) internal {
        string[] storage subnames = ownerToSubnames[owner];
        for (uint256 i = 0; i < subnames.length; i++) {
            if (keccak256(bytes(subnames[i])) == keccak256(bytes(subname))) {
                subnames[i] = subnames[subnames.length - 1];
                subnames.pop();
                break;
            }
        }
    }
    
    /**
     * @dev Withdraw collected fees (admin only)
     */
    function withdrawFees() external onlyAdmin {
        uint256 balance = address(this).balance;
        require(balance > 0, "SubnameRegistrar: No fees to withdraw");
        
        (bool success, ) = payable(owner()).call{value: balance}("");
        require(success, "SubnameRegistrar: Failed to withdraw fees");
    }
    
    /**
     * @dev Pause the contract (admin only)
     */
    function pause() external onlyAdmin {
        _pause();
    }
    
    /**
     * @dev Unpause the contract (admin only)
     */
    function unpause() external onlyAdmin {
        _unpause();
    }
    
    /**
     * @dev Receive function to accept ETH
     */
    receive() external payable {}
}
","keccak256":"0xf3c049db4a39dcec611c9d1ae6e83df7b9d1f626ee3316c2282e8c276d50b95c","license":"MIT"},"contracts/VendorRegistry.sol":{"content":"//SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

/**
 * @title VendorRegistry
 * @dev Registry for managing vendor profiles and verification status
 * Stores vendor information including ENS names, phone verification, EFP verification, and EFPas scores
 */
contract VendorRegistry is Ownable, Pausable {
    // Vendor status enum
    enum VendorStatus {
        Pending,    // 0 - Vendor registered but not approved
        Active,     // 1 - Vendor approved and active
        Suspended,  // 2 - Vendor suspended by admin
        Rejected    // 3 - Vendor registration rejected
    }
    
    // Vendor profile structure
    struct VendorProfile {
        address wallet;           // Vendor's wallet address
        string ensName;          // ENS subname (e.g., "business.tapngo.eth")
        string businessName;     // Business display name
        string phoneHash;        // Hashed phone number for verification
        bool phoneVerified;      // Phone verification status
        bool efpVerified;        // Ethereum Follow Protocol verification
        uint256 efpasScore;      // EFPas reputation score (0 if not available)
        VendorStatus status;     // Current vendor status
        uint256 registrationTime; // Timestamp of registration
        uint256 lastUpdated;     // Timestamp of last profile update
    }
    
    // State variables
    mapping(address => VendorProfile) public vendors;
    mapping(string => address) public ensToVendor; // ENS name -> vendor address
    mapping(string => bool) public usedPhoneHashes; // Track used phone hashes
    address[] public vendorAddresses; // Array of all vendor addresses
    
    // Verification requirements
    bool public requirePhoneVerification = true;
    bool public requireEFPVerification = true;
    uint256 public minEFPasScore = 0; // Minimum EFPas score required (0 = not required)
    
    // Events
    event VendorRegistered(
        address indexed vendor,
        string ensName,
        string businessName,
        string phoneHash
    );
    
    event VendorApproved(address indexed vendor, string ensName);
    event VendorSuspended(address indexed vendor, string reason);
    event VendorRejected(address indexed vendor, string reason);
    event VendorUpdated(address indexed vendor, string field);
    event PhoneVerified(address indexed vendor, string phoneHash);
    event EFPVerified(address indexed vendor, bool verified);
    event EFPasScoreUpdated(address indexed vendor, uint256 score);
    
    // Modifiers
    modifier onlyVendor() {
        require(vendors[msg.sender].wallet != address(0), "VendorRegistry: Not a registered vendor");
        _;
    }
    
    modifier onlyActiveVendor() {
        require(
            vendors[msg.sender].wallet != address(0) && 
            vendors[msg.sender].status == VendorStatus.Active,
            "VendorRegistry: Not an active vendor"
        );
        _;
    }
    
    modifier onlyAdmin() {
        require(msg.sender == owner(), "VendorRegistry: Only admin can call this function");
        _;
    }
    
    constructor() Ownable(msg.sender) {}
    
    /**
     * @dev Register a new vendor
     * @param ensName ENS subname for the vendor (e.g., "business.tapngo.eth")
     * @param businessName Display name of the business
     * @param phoneHash Hashed phone number
     */
    function registerVendor(
        string memory ensName,
        string memory businessName,
        string memory phoneHash
    ) external whenNotPaused {
        require(vendors[msg.sender].wallet == address(0), "VendorRegistry: Vendor already registered");
        require(bytes(ensName).length > 0, "VendorRegistry: ENS name cannot be empty");
        require(bytes(businessName).length > 0, "VendorRegistry: Business name cannot be empty");
        require(bytes(phoneHash).length > 0, "VendorRegistry: Phone hash cannot be empty");
        require(ensToVendor[ensName] == address(0), "VendorRegistry: ENS name already taken");
        require(!usedPhoneHashes[phoneHash], "VendorRegistry: Phone number already used");
        
        // Create vendor profile
        VendorProfile memory newVendor = VendorProfile({
            wallet: msg.sender,
            ensName: ensName,
            businessName: businessName,
            phoneHash: phoneHash,
            phoneVerified: false,
            efpVerified: false,
            efpasScore: 0,
            status: VendorStatus.Pending,
            registrationTime: block.timestamp,
            lastUpdated: block.timestamp
        });
        
        vendors[msg.sender] = newVendor;
        ensToVendor[ensName] = msg.sender;
        usedPhoneHashes[phoneHash] = true;
        vendorAddresses.push(msg.sender);
        
        emit VendorRegistered(msg.sender, ensName, businessName, phoneHash);
    }
    
    /**
     * @dev Approve a vendor (admin only)
     * @param vendorAddress Address of the vendor to approve
     */
    function approveVendor(address vendorAddress) external onlyAdmin {
        require(vendors[vendorAddress].wallet != address(0), "VendorRegistry: Vendor not found");
        require(vendors[vendorAddress].status == VendorStatus.Pending, "VendorRegistry: Vendor not pending");
        
        // Check verification requirements
        if (requirePhoneVerification) {
            require(vendors[vendorAddress].phoneVerified, "VendorRegistry: Phone verification required");
        }
        if (requireEFPVerification) {
            require(vendors[vendorAddress].efpVerified, "VendorRegistry: EFP verification required");
        }
        if (minEFPasScore > 0) {
            require(vendors[vendorAddress].efpasScore >= minEFPasScore, "VendorRegistry: EFPas score too low");
        }
        
        vendors[vendorAddress].status = VendorStatus.Active;
        vendors[vendorAddress].lastUpdated = block.timestamp;
        
        emit VendorApproved(vendorAddress, vendors[vendorAddress].ensName);
    }
    
    /**
     * @dev Suspend a vendor (admin only)
     * @param vendorAddress Address of the vendor to suspend
     * @param reason Reason for suspension
     */
    function suspendVendor(address vendorAddress, string memory reason) external onlyAdmin {
        require(vendors[vendorAddress].wallet != address(0), "VendorRegistry: Vendor not found");
        require(vendors[vendorAddress].status == VendorStatus.Active, "VendorRegistry: Vendor not active");
        
        vendors[vendorAddress].status = VendorStatus.Suspended;
        vendors[vendorAddress].lastUpdated = block.timestamp;
        
        emit VendorSuspended(vendorAddress, reason);
    }
    
    /**
     * @dev Reject a vendor registration (admin only)
     * @param vendorAddress Address of the vendor to reject
     * @param reason Reason for rejection
     */
    function rejectVendor(address vendorAddress, string memory reason) external onlyAdmin {
        require(vendors[vendorAddress].wallet != address(0), "VendorRegistry: Vendor not found");
        require(vendors[vendorAddress].status == VendorStatus.Pending, "VendorRegistry: Vendor not pending");
        
        vendors[vendorAddress].status = VendorStatus.Rejected;
        vendors[vendorAddress].lastUpdated = block.timestamp;
        
        emit VendorRejected(vendorAddress, reason);
    }
    
    /**
     * @dev Update vendor's phone verification status (admin only)
     * @param vendorAddress Address of the vendor
     * @param verified Verification status
     */
    function updatePhoneVerification(address vendorAddress, bool verified) external onlyAdmin {
        require(vendors[vendorAddress].wallet != address(0), "VendorRegistry: Vendor not found");
        
        vendors[vendorAddress].phoneVerified = verified;
        vendors[vendorAddress].lastUpdated = block.timestamp;
        
        emit PhoneVerified(vendorAddress, vendors[vendorAddress].phoneHash);
    }
    
    /**
     * @dev Update vendor's EFP verification status (admin only)
     * @param vendorAddress Address of the vendor
     * @param verified Verification status
     */
    function updateEFPVerification(address vendorAddress, bool verified) external onlyAdmin {
        require(vendors[vendorAddress].wallet != address(0), "VendorRegistry: Vendor not found");
        
        vendors[vendorAddress].efpVerified = verified;
        vendors[vendorAddress].lastUpdated = block.timestamp;
        
        emit EFPVerified(vendorAddress, verified);
    }
    
    /**
     * @dev Update vendor's EFPas score (admin only)
     * @param vendorAddress Address of the vendor
     * @param score EFPas reputation score
     */
    function updateEFPasScore(address vendorAddress, uint256 score) external onlyAdmin {
        require(vendors[vendorAddress].wallet != address(0), "VendorRegistry: Vendor not found");
        
        vendors[vendorAddress].efpasScore = score;
        vendors[vendorAddress].lastUpdated = block.timestamp;
        
        emit EFPasScoreUpdated(vendorAddress, score);
    }
    
    /**
     * @dev Update business name (vendor only)
     * @param newBusinessName New business name
     */
    function updateBusinessName(string memory newBusinessName) external onlyVendor {
        require(bytes(newBusinessName).length > 0, "VendorRegistry: Business name cannot be empty");
        
        vendors[msg.sender].businessName = newBusinessName;
        vendors[msg.sender].lastUpdated = block.timestamp;
        
        emit VendorUpdated(msg.sender, "businessName");
    }
    
    /**
     * @dev Get vendor profile by address
     * @param vendorAddress Address of the vendor
     * @return profile Vendor profile struct
     */
    function getVendorProfile(address vendorAddress) external view returns (VendorProfile memory profile) {
        require(vendors[vendorAddress].wallet != address(0), "VendorRegistry: Vendor not found");
        return vendors[vendorAddress];
    }
    
    /**
     * @dev Get vendor address by ENS name
     * @param ensName ENS name to look up
     * @return vendorAddress Address of the vendor
     */
    function getVendorByENS(string memory ensName) external view returns (address vendorAddress) {
        return ensToVendor[ensName];
    }
    
    /**
     * @dev Check if an address is a registered vendor
     * @param vendorAddress Address to check
     * @return isRegistered True if address is a registered vendor
     */
    function isVendor(address vendorAddress) external view returns (bool isRegistered) {
        return vendors[vendorAddress].wallet != address(0);
    }
    
    /**
     * @dev Check if a vendor is active
     * @param vendorAddress Address to check
     * @return isActive True if vendor is active
     */
    function isActiveVendor(address vendorAddress) external view returns (bool isActive) {
        return vendors[vendorAddress].wallet != address(0) && 
               vendors[vendorAddress].status == VendorStatus.Active;
    }
    
    /**
     * @dev Get total number of registered vendors
     * @return count Total number of vendors
     */
    function getVendorCount() external view returns (uint256 count) {
        return vendorAddresses.length;
    }
    
    /**
     * @dev Get all vendor addresses (for pagination)
     * @param offset Starting index
     * @param limit Maximum number of addresses to return
     * @return addresses Array of vendor addresses
     */
    function getVendorAddresses(uint256 offset, uint256 limit) external view returns (address[] memory addresses) {
        require(offset < vendorAddresses.length, "VendorRegistry: Offset out of bounds");
        
        uint256 end = offset + limit;
        if (end > vendorAddresses.length) {
            end = vendorAddresses.length;
        }
        
        address[] memory result = new address[](end - offset);
        for (uint256 i = offset; i < end; i++) {
            result[i - offset] = vendorAddresses[i];
        }
        
        return result;
    }
    
    /**
     * @dev Update verification requirements (admin only)
     * @param phoneRequired Whether phone verification is required
     * @param efpRequired Whether EFP verification is required
     * @param minScore Minimum EFPas score required
     */
    function updateVerificationRequirements(
        bool phoneRequired,
        bool efpRequired,
        uint256 minScore
    ) external onlyAdmin {
        requirePhoneVerification = phoneRequired;
        requireEFPVerification = efpRequired;
        minEFPasScore = minScore;
    }
    
    /**
     * @dev Pause the contract (admin only)
     */
    function pause() external onlyAdmin {
        _pause();
    }
    
    /**
     * @dev Unpause the contract (admin only)
     */
    function unpause() external onlyAdmin {
        _unpause();
    }
}
","keccak256":"0xd489b176a6aaa264ba089ce8665709ea750694f864c54727aefa5aa8f5d6497a","license":"MIT"},"contracts/bUSDC.sol":{"content":"//SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

/**
 * @title bUSDC - Base USDC Test Token
 * @dev A mintable ERC20 token for testing Tap&Go Pay on Base Sepolia
 * This is a test stablecoin that simulates USDC functionality
 */
contract bUSDC is ERC20, Ownable, Pausable {
    // Maximum supply for the test token (1 billion tokens)
    uint256 public constant MAX_SUPPLY = 1_000_000_000 * 10**6; // 6 decimals like real USDC
    
    // Faucet limits
    uint256 public constant FAUCET_AMOUNT = 1000 * 10**6; // 1000 tokens per faucet
    uint256 public constant FAUCET_COOLDOWN = 24 hours; // 24 hour cooldown between faucet claims
    
    // Mapping to track last faucet claim time per address
    mapping(address => uint256) public lastFaucetClaim;
    
    // Events
    event FaucetClaimed(address indexed user, uint256 amount);
    event TokensMinted(address indexed to, uint256 amount);
    event TokensBurned(address indexed from, uint256 amount);
    
    constructor() ERC20("Base USDC Test", "bUSDC") Ownable(msg.sender) {
        // Mint initial supply to deployer for testing
        _mint(msg.sender, 100_000 * 10**6); // 100k tokens
    }
    
    /**
     * @dev Mint tokens to a specific address (only owner)
     * @param to Address to mint tokens to
     * @param amount Amount of tokens to mint (in wei, 6 decimals)
     */
    function mint(address to, uint256 amount) external onlyOwner {
        require(totalSupply() + amount <= MAX_SUPPLY, "bUSDC: Exceeds max supply");
        _mint(to, amount);
        emit TokensMinted(to, amount);
    }
    
    /**
     * @dev Burn tokens from a specific address (only owner)
     * @param from Address to burn tokens from
     * @param amount Amount of tokens to burn (in wei, 6 decimals)
     */
    function burn(address from, uint256 amount) external onlyOwner {
        _burn(from, amount);
        emit TokensBurned(from, amount);
    }
    
    /**
     * @dev Faucet function - allows users to claim test tokens
     * Users can claim once every 24 hours
     */
    function claimFaucet() external whenNotPaused {
        require(
            block.timestamp >= lastFaucetClaim[msg.sender] + FAUCET_COOLDOWN,
            "bUSDC: Faucet cooldown not expired"
        );
        require(
            totalSupply() + FAUCET_AMOUNT <= MAX_SUPPLY,
            "bUSDC: Faucet would exceed max supply"
        );
        
        lastFaucetClaim[msg.sender] = block.timestamp;
        _mint(msg.sender, FAUCET_AMOUNT);
        
        emit FaucetClaimed(msg.sender, FAUCET_AMOUNT);
    }
    
    /**
     * @dev Check if an address can claim from faucet
     * @param user Address to check
     * @return canClaim True if user can claim from faucet
     * @return timeUntilClaim Time until next claim is available (0 if can claim now)
     */
    function canClaimFaucet(address user) external view returns (bool canClaim, uint256 timeUntilClaim) {
        uint256 lastClaim = lastFaucetClaim[user];
        uint256 nextClaimTime = lastClaim + FAUCET_COOLDOWN;
        
        if (block.timestamp >= nextClaimTime) {
            canClaim = true;
            timeUntilClaim = 0;
        } else {
            canClaim = false;
            timeUntilClaim = nextClaimTime - block.timestamp;
        }
    }
    
    /**
     * @dev Pause the contract (only owner)
     */
    function pause() external onlyOwner {
        _pause();
    }
    
    /**
     * @dev Unpause the contract (only owner)
     */
    function unpause() external onlyOwner {
        _unpause();
    }
    
    /**
     * @dev Override transfer to respect pause state
     */
    function _update(address from, address to, uint256 value) internal override whenNotPaused {
        super._update(from, to, value);
    }
    
    /**
     * @dev Get the number of decimals for this token (6, like real USDC)
     */
    function decimals() public pure override returns (uint8) {
        return 6;
    }
}
